/**
 * @since 2023-08-06
 * @author vivaxy
 * Because:
 *  - ⌊y/17⌋ is a big number.
 *  - 2 ** (-17⌊x⌋ - mod(⌊y⌋, 17)) could be a fraction.
 *  - BigInt cannot be calculated with fractions.
 * Therefore:
 *  - BigInt is not applicable.
 *  - Try BigNumber
 */
/**
 * @param {BigInt} x
 * @param {BigInt} y
 * @returns {boolean}
 */
export function formula(x, y) {
  const left = new BigNumber(1 / 2);
  const right2Exp = new BigNumber(-17)
    .multipliedBy(new BigNumber(x).toFixed(0, BigNumber.ROUND_FLOOR))
    .minus(new BigNumber(new BigNumber(y).toFixed()).modulo(17));
  const right1 = new BigNumber(
    new BigNumber(y).dividedBy(17).toFixed(0, BigNumber.ROUND_FLOOR),
  );
  const right2 = new BigNumber(2).exponentiatedBy(right2Exp);
  const right = new BigNumber(right1.multipliedBy(right2))
    .modulo(2)
    .toFixed(0, BigNumber.ROUND_FLOOR);
  return left.isLessThan(right);
}

